oscarWilde13 = Quote.create({ content: "You don't love someone for their looks, or their clothes, or for their fancy car, but because they sing a song only you can hear.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde14 = Quote.create({ content: "An idea that is not dangerous is unworthy of being called an idea at all.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde15 = Quote.create({ content: "I never travel without my diary. One should always have something sensational to read in the train.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde16 = Quote.create({ content: "Life is too important to be taken seriously.", author: 'Oscar Wilde', quote_details: "" })

oscarWilde17 = Quote.create({ content: "To define is to limit.", author: 'Oscar Wilde', quote_details: "" })

