oscarWilde24 = Quote.create({ content: "I love acting. It is so much more real than life.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde25 = Quote.create({ content: "I don't want to go to heaven. None of my friends are there.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde26 = Quote.create({ content: "Always forgive your enemies. Nothing annoys them so much.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde27 = Quote.create({ content: "To expect the unexpected shows a thoroughly modern intellect.", author: 'Oscar Wilde', quote_details: "" })
oscarWilde28 = Quote.create({ content: "The books that the world calls immoral are books that show the world its own shame", author: 'Oscar Wilde', quote_details: "`The Picture of Dorain Gray`" })
oscarWilde29 = Quote.create({ content: "How can a woman be expected to be happy with a man who insists on treating her as if she were a perfectly normal human being.", author: 'Oscar Wilde', quote_details: ""})
